mapper.select:
        select * from(
	        select * from t_zh_grid_forecast where forecasttime=to_date(#{forecasttime},'yyyy-mm-dd hh24:mi:ss') 
	        and venueid=#{venueid}
	        order by ddatetime desc,venueid asc,forecasttime asc
        )
        where  rownum<=1   order by rownum asc

       select * from(
	        select * from t_zh_grid_forecast where venueid=#{venueid}  
	        order by ddatetime desc,venueid asc,forecasttime asc
        )
        where  rownum<=88   order by rownum asc
