mapper.select:
        	select obtid, to_char(ddatetime,'yyyy-mm-dd hh24:mi:ss') as ddatetime,WDIDF,rain,vis 
        	from (
	        	select * from WEICHAO.t_bridge_data
	        	where obtid=#{obtid} 
	        	order by ddatetime desc
	        	) 
        	where  rownum<=288   order by rownum asc

        	select obtid, to_char(ddatetime,'yyyy-mm-dd hh24:mi:ss') as ddatetime,WDIDF,rain,vis 
        	from (
	        	select * from WEICHAO.t_bridge_data
	        	where obtid=#{obtid} 
	        	order by ddatetime desc
	        	) 
        	where  rownum<=1   order by rownum asc
